	const slider = document.querySelector(".destaques_items");
	const slides = document.querySelectorAll(".destaques_items .destaques_item");
	const button = document.querySelectorAll(".destaques_button");

	let current = 0;
	let prev = slides.length - 1;
	let next = 1;

	for (let i = 0; i < button.length; i++) {
		button[i].addEventListener("click", () => i == 0 ? gotoPrev() : gotoNext());
	}

	const gotoPrev = () => {
		if(current > 0){
			gotoNum(current - 1)
		}else{
			gotoNum(slides.length - 1)
		}
	}

	const gotoNext = () => {
		if(current < slides.length - 1){
			gotoNum(current + 1)
		}else{
			gotoNum(0)
		}
	}

	const gotoNum = number => {
		current = number;
		prev = current - 1;
		next = current + 1;

		for (let i = 0; i < slides.length; i++) {
			slides[i].classList.remove("destaques_active");
			slides[i].classList.remove("destaques_prev");
			slides[i].classList.remove("destaques_next");
		}

		if (next == slides.length) {
			next = 0;
		}

		if (prev == -1) {
			prev = slides.length - 1;
		}

		slides[current].classList.add("destaques_active");
		slides[prev].classList.add("destaques_prev");
		slides[next].classList.add("destaques_next");
	}


